import React, { useState } from 'react';
import { Container, AppBar, Typography, Grow, Grid } from '@material-ui/core';
import useStyles from './styles.js';
import memories from '../images/memories.jpg';
import CustomerHome from './Customer/CustomerHome/CustomerHome.js';
import CustomerOrders from './Customer/CustomerOrders/CustomerOrders';
import CustomerProfile from './Customer/CustomerProfile/CustomerProfile.js';
import CustomerCart from './Customer/CustomerCart/CustomerCart.js';
import CustomerContact from './Customer/CustomerContact/CustomerContact.js';
import styled from "styled-components";
import { NavLink, Link } from "react-router-dom";


const Navigation = styled.header`
  width: 100%;
  border-bottom: 10px solid #222;
  z-index: 1;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0px 100px 0;
  height: 140px;
  margin-bottom: 60px;
  background: #f8f8f8;

  .logo a {
    padding-top: 33px;
    display: flex;
    flex-direction: column;
    clear: both;
    padding-bottom: 30px;
    text-decoration: none;

    p {
      width: 500px;
      display: block;
    }
    em {
      font-size: 0.5em;
      float: left;
      display: block;
      img {
        display: inline-block;
        margin-top: 5px;
        width: 15px;
        float: left;
      }
      .letterhead {
        display: inline-block;
        line-height: 260%;
        float: left;
      }
    }
  }
  .gray {
    color: #ccc;
  }
  a {
    color: #222;
    opacity: 0.55;
    transition: all 0.6s;
    color: #222;
    font-size: 1em;
  }
  a:hover {
    opacity: 1;
  }
  .fa-bars {
    display: none;
    color: #222;
    font-size: 2rem;
  }
  nav {
    ul {
      display: flex;
      justify-content: space-between;
    }
    li {
      margin: 0 15px;
      justify-content: space-between;
      font-size: 1em;
    }
    a {
      font-size: 1em;
      text-decoration: none;
      .active {
        color: tomato;
      }
    }
    a.active {
      color: #222;
    }
  }

  @media only screen and (max-width: 800px) {
    padding: 0px;
    .logo {
      padding-left: 15px;
      padding-top: 0px !important;
    }
  }
  @media only screen and (max-width: 600px) {
    height: auto;
    min-height: 50px;
    display: block;
    position: relative;
    .logo {
      width: 100%;
      display: block;
      padding-top: 20px;
      margin: 0px;
      margin-left: -5px;
      a {
        padding: 20px 0px;
      }
    }
    .fa-bars {
      display: inline-block;
      position: absolute;
      top: 10px;
      right: 10px;
      cursor: pointer;
    }
    ul.collapsed {
      width: 100%;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      flex-wrap: wrap;

      overflow: hidden;
      max-height: 0;
      -moz-transition-duration: 0.4s;
      -webkit-transition-duration: 0.4s;
      -o-transition-duration: 0.4s;
      transition-duration: 0.4s;
      -moz-transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
      -webkit-transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
      -o-transition-timing-function: cubic-bezier(0, 1, 0.5, 1);
      transition-timing-function: cubic-bezier(0, 1, 0.5, 1);

      &.is-expanded {
        overflow: hidden;
        max-height: 500px; /* approximate max height */
        -moz-transition-duration: 0.4s;
        -webkit-transition-duration: 0.4s;
        -o-transition-duration: 0.4s;
        transition-duration: 0.4s;
        -moz-transition-timing-function: ease-in;
        -webkit-transition-timing-function: ease-in;
        -o-transition-timing-function: ease-in;
        transition-timing-function: ease-in;
      }
      li {
        padding: 15px 10px;
        margin: 0px 0px;
        width: 100%;
      }
    }
  }
`;

const Homepage = ({ username, userid, logout }) => {

    const [selectedButton, setSelectedButton] = useState(<CustomerHome userid = {userid} />);
    const [noOfItems, setNoOfItems] = useState(15);
    const classes = useStyles();
    const [isExpanded, setIsExpanded] = useState(false);

    const handleToggle = (e) => {
        e.preventDefault();
        setIsExpanded(!isExpanded);
      }

    return(
        <div>
            <h1>Homepage</h1>
            <h3>Welcome {username}</h3>
            <Container maxWidth="lg">
                <AppBar className={classes.appBar} position="static" color="inherit">
                    <Typography className={classes.heading} variant="h2" align="center">Shopaholic</Typography>
                    <img className={classes.image} src={memories} alt="memories" height="60"></img>
                </AppBar>
            </Container>
            <div>


            <Navigation>
                <div className="logo">
                <Link to="/">
                    <p>React Responsive Navigation</p>
                    <em>
                    <div className="letterhead">
                        <span className="name">kentorry</span>
                        <span className="gray">.io</span>
                    </div>
                    </em>
                </Link>
                </div>
                <nav className="nav">
                <i
                    className="fa fa-bars"
                    aria-hidden="true"
                    onClick={e => handleToggle(e)}
                />
                <ul className={`collapsed ${isExpanded ? "is-expanded" : ""}`}>
                    <NavLink activeClassName="active" to="/">
                    <li>home</li>
                    </NavLink>
                    <NavLink activeClassName="active" to="/about">
                    <li>about</li>
                    </NavLink>
                    <NavLink activeClassName="active" to="/contact">
                    <li>contact</li>
                    </NavLink>
                </ul>
                </nav>
            </Navigation>


                <button onClick = {e => setSelectedButton(<CustomerHome userid = {userid} />)}>Home</button>
                <button onClick = {e => setSelectedButton(<CustomerOrders userid = {userid} />)}>My Orders</button>
                <button onClick = {e => setSelectedButton(<CustomerProfile userid = {userid} />)}>My Profile</button>
                <button onClick = {e => setSelectedButton(<CustomerCart userid = {userid} setSelectedButton={setSelectedButton} />)}>Cart {noOfItems}</button>
                <button onClick = {e => setSelectedButton(<CustomerContact userid = {userid} />)}>Contact Us</button>

            </div>
            <div>
                { selectedButton }
            </div>
            <button onClick={logout}>Logout</button>

        </div>
    )
}

export default Homepage;
