import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { CircularProgress } from '@material-ui/core';

import { makeStyles, useTheme, withTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import AddIcon from '@material-ui/icons/Add';
import SubtractIcon from '@material-ui/icons/Remove';
import DeleteIcon from '@material-ui/icons/Delete';

import image from '../../../images/image2.JPG';
import { Button } from '@material-ui/core';
import CartItem from './CartItem';

const CustomerCart = ({ userid }) => {

    const useStyles = makeStyles((theme) => ({
        root: {
          display: 'flex',
        },
        details: {
          display: 'flex',
          flexDirection: 'column',
          width: '65%',
        },
        content: {
          flex: '1 0 auto',
        },
        cover: {
          width: 151,
        },
        controls: {
          display: 'flex',
          alignItems: 'center',
          paddingLeft: theme.spacing(1),
          paddingBottom: theme.spacing(1),
        },
        container: {
            width: '80%',
            margin: 'auto',
        },
        price: {
            color: 'white',
            fontWeight: 'bold',
            backgroundColor: 'maroon',
            padding: '10px',
            width: '70px',
            borderRadius: '15%',
        }
      }));

      const classes = useStyles();

      const [cartDetails, setCartDetails] = useState({});

      const getCartDetails = () => {
        axios({
            method: "GET",
            url: "http://localhost:5000/cart/fetch/"+userid, 
          }).then(res => {
              console.log(res.data);
              if(!res.data.error){
                setCartDetails(res.data);
              }
          });
      }

      useEffect(() => {
        getCartDetails();
        }, [])


    return (
        <div className={classes.container}>
            <h3>This is Customer Cart</h3>
            <p>Total Items in Cart: {cartDetails.totalItems}</p>
            <p>Total Cost: {cartDetails.totalCost}</p>
            {
            cartDetails ? 
                cartDetails.items.map((cart) => (
                    <CartItem cart={cart} />
                   ))
             : (
                <CircularProgress /> 
            )
            }
           

        </div>
    )
}

export default CustomerCart;
