import React, { useState, useEffect } from 'react';
import { Container, AppBar, Typography, Grow, Grid } from '@material-ui/core';
import memories from '../images/memories.jpg';
import CustomerHome from './Customer/CustomerHome/CustomerHome.js';
import CustomerOrders from './Customer/CustomerOrders/CustomerOrders';
import CustomerProfile from './Customer/CustomerProfile/CustomerProfile.js';
import CustomerCart from './Customer/CustomerCart/CustomerCart.js';
import CustomerContact from './Customer/CustomerContact/CustomerContact.js';
import {
    Toolbar,
    makeStyles,
    Button,
    IconButton,
    Drawer,
    Link as MLink,
    MenuItem,
  } from "@material-ui/core";
import MenuIcon from "@material-ui/icons/Menu";
import { Link as RouterLink } from "react-router-dom";

const Homepage = ({ username, userid, logout }) => {

    const headersData = [
        {
          label: "Listings",
          href: "/listings",
        },
        {
          label: "Mentors",
          href: "/mentors",
        },
        {
          label: "My Account",
          href: "/account",
        },
        {
          label: "Log Out",
          href: "/logout",
        },
      ];

      const useStyles = makeStyles((theme) => ({
        header: {
          backgroundColor: "#400CCC",
          paddingRight: "79px",
          paddingLeft: "118px",
          "@media (max-width: 900px)": {
            paddingLeft: 0,
          },
        },
        logo: {
          fontFamily: "Work Sans, sans-serif",
          fontWeight: 600,
          color: "#FFFEFE",
          textAlign: "left",
        },
        menuButton: {
          fontFamily: "Open Sans, sans-serif",
          fontWeight: 700,
          size: "18px",
          marginLeft: "38px",
        },
        toolbar: {
          display: "flex",
          justifyContent: "space-between",
        },
        drawerContainer: {
          padding: "20px 30px",
        },
        appBar: {
            borderRadius: 15,
            margin: '30px 0',
            display: 'flex',
            flexDirection: 'row',
            justifyContent: 'center',
            alignItems: 'center',
          },
          heading: {
            color: 'rgba(0,183,255, 1)',
          },
          image: {
            marginLeft: '15px',
          },
          [theme.breakpoints.down('sm')] : {
            mainContainer: {
              flexDirection: "column-reverse",
            },
          }
      }));

      const { header, logo, menuButton, toolbar, drawerContainer } = useStyles();

        const [state, setState] = useState({
            mobileView: false,
            drawerOpen: false,
        });

        const { mobileView, drawerOpen } = state;

        useEffect(() => {
            const setResponsiveness = () => {
            return window.innerWidth < 900
                ? setState((prevState) => ({ ...prevState, mobileView: true }))
                : setState((prevState) => ({ ...prevState, mobileView: false }));
            };

            setResponsiveness();

            window.addEventListener("resize", () => setResponsiveness());
        }, []);

        const displayDesktop = () => {
            return (
              <Toolbar className={toolbar}>
                {ShopaholicLogo}
                <div>{getMenuButtons()}</div>
              </Toolbar>
            );
          };
        
          const displayMobile = () => {
            const handleDrawerOpen = () =>
              setState((prevState) => ({ ...prevState, drawerOpen: true }));
            const handleDrawerClose = () =>
              setState((prevState) => ({ ...prevState, drawerOpen: false }));
        
            return (
              <Toolbar>
                <IconButton
                  {...{
                    edge: "start",
                    color: "inherit",
                    "aria-label": "menu",
                    "aria-haspopup": "true",
                    onClick: handleDrawerOpen,
                  }}
                >
                  <MenuIcon />
                </IconButton>
        
                <Drawer
                  {...{
                    anchor: "left",
                    open: drawerOpen,
                    onClose: handleDrawerClose,
                  }}
                >
                  <div className={drawerContainer}>{getDrawerChoices()}</div>
                </Drawer>
        
                <div>{ShopaholicLogo}</div>
              </Toolbar>
            );
          };
        
          const getDrawerChoices = () => {
            return headersData.map(({ label, href }) => {
              return (
                <MLink
                  {...{
                    //component: RouterLink,
                    to: href,
                    color: "inherit",
                    style: { textDecoration: "none" },
                    key: label,
                  }}
                >
                  <MenuItem>{label}</MenuItem>
                </MLink>
              );
            });
          };
        
          const ShopaholicLogo = (
            <Typography variant="h6" component="h1" className={logo}>
              Shopaholic
            </Typography>
          );
        
          const getMenuButtons = () => {
            return headersData.map(({ label, href }) => {
              return (
                <Button
                  {...{
                    key: label,
                    color: "inherit",
                    to: href,
                    //component: RouterLink,
                    className: menuButton,
                  }}
                >
                  {label}
                </Button>
              );
            });
          };


    const [selectedButton, setSelectedButton] = useState(<CustomerHome userid = {userid} />);
    const [noOfItems, setNoOfItems] = useState(15);
    const classes = useStyles();

    return(
        <div>
            <h1>Homepage</h1>
            <h3>Welcome {username}</h3>

            <header>
      <AppBar className={header}>
        {mobileView ? displayMobile() : displayDesktop()}
      </AppBar>
    </header>


            <Container maxWidth="lg">
                <AppBar className={classes.appBar} position="static" color="inherit">
                    <Typography className={classes.heading} variant="h2" align="center">Shopaholic</Typography>
                    <img className={classes.image} src={memories} alt="memories" height="60"></img>
                </AppBar>
            </Container>
            <div>

                <button onClick = {e => setSelectedButton(<CustomerHome userid = {userid} />)}>Home</button>
                <button onClick = {e => setSelectedButton(<CustomerOrders userid = {userid} />)}>My Orders</button>
                <button onClick = {e => setSelectedButton(<CustomerProfile userid = {userid} />)}>My Profile</button>
                <button onClick = {e => setSelectedButton(<CustomerCart userid = {userid} setSelectedButton={setSelectedButton} />)}>Cart {noOfItems}</button>
                <button onClick = {e => setSelectedButton(<CustomerContact userid = {userid} />)}>Contact Us</button>

            </div>
            <div>
                { selectedButton }
            </div>
            <button onClick={logout}>Logout</button>

        </div>
    )
}

export default Homepage;
