import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import AddIcon from '@material-ui/icons/Add';
import SubtractIcon from '@material-ui/icons/Remove';
import DeleteIcon from '@material-ui/icons/Delete';

import { Button } from '@material-ui/core';
import { useEffect, useState } from 'react';

const CartItemDetails = ({ product, setDetails}) => {

    const useStyles = makeStyles((theme) => ({
        root: {
          display: 'flex',
        },
        details: {
          display: 'flex',
          flexDirection: 'column',
          width: '65%',
        },
        content: {
          flex: '1 0 auto',
        },
        cover: {
          width: 351,
          cursor: 'pointer',
        },
        controls: {
          display: 'flex',
          alignItems: 'center',
          paddingLeft: theme.spacing(1),
          paddingBottom: theme.spacing(1),
        },
        container: {
            width: '100%',
            margin: 'auto',
        },
        price: {
            color: 'white',
            fontWeight: 'bold',
            backgroundColor: 'maroon',
            padding: '10px',
            width: '70px',
            borderRadius: '15%',
        }
      }));

      const classes = useStyles();

    return (
        <div>
            <button onClick={e => setDetails(false)}>Back to Cart</button>
            <Card className={classes.root}>
                <CardMedia
                    className={classes.cover}
                    image={product.picture}
                    title="Product Details"
                />
                <div className={classes.details}>
                    <CardContent className={classes.content}>
                    <Typography component="h5" variant="h5">
                        {product.title}
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        {product.description}
                    </Typography>
                    <Typography variant="subtitle1" className={classes.price}>
                        {product.price}
                    </Typography>
                    </CardContent>
                    <div className={classes.controls}>
                    <div>
                    <IconButton aria-label="previous">
                        <AddIcon /> 
                    </IconButton>
                    <IconButton aria-label="play/pause">
                        <SubtractIcon />
                    </IconButton>
                    <IconButton aria-label="next">
                        <DeleteIcon />
                    </IconButton>
                    </div>

                    </div>
                </div>
                <div style={{marginTop: '50px', display:'flex', flexDirection:'column'}}>
                    <Typography variant="subtitle1">
                        Qty
                    </Typography>
                    <Typography variant="subtitle1">
                        Total Price
                    </Typography>
                    <Button style={{backgroundColor: '#FFC300'}}>Proceed to Buy</Button>
                </div>
            </Card>
        </div>
    )
}

export default CartItemDetails;
