import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { CircularProgress } from '@material-ui/core';

import { makeStyles, useTheme, withTheme } from '@material-ui/core/styles';

import CartItem from './CartItem';
import CartItemDetails from './CartItemDetails';
import product from '../../../../../../backend/models/product';


const CustomerCart = ({ userid }) => {

    const useStyles = makeStyles((theme) => ({
        root: {
          display: 'flex',
        },
        details: {
          display: 'flex',
          flexDirection: 'column',
          width: '65%',
        },
        content: {
          flex: '1 0 auto',
        },
        cover: {
          width: 151,
        },
        controls: {
          display: 'flex',
          alignItems: 'center',
          paddingLeft: theme.spacing(1),
          paddingBottom: theme.spacing(1),
        },
        container: {
            width: '80%',
            margin: 'auto',
        },
        price: {
            color: 'white',
            fontWeight: 'bold',
            backgroundColor: 'maroon',
            padding: '10px',
            width: '70px',
            borderRadius: '15%',
        }
      }));

      const classes = useStyles();

      const [cartDetails, setCartDetails] = useState(null);
      const [details, setDetails] = useState(false);
      const [productDetails, setProductDetails] = useState(null);

      const getCartDetails = () => {
        axios({
            method: "GET",
            url: "http://localhost:5000/cart/fetch/"+userid, 
          }).then(res => {
              console.log(res.data);
              if(!res.data.error){
                setCartDetails(res.data);
              }
          });
      }

      useEffect(() => {
        getCartDetails();
        }, []);

      const showDetails = (product) => {
          setDetails(true);
          setProductDetails(product);
      }


    return (
        <div className={classes.container}>
            <h3>This is Customer Cart</h3>
            {
            cartDetails ?  (
            <div>
            <p>Total Items in Cart: {cartDetails.totalItems}</p>
            <p>Total Cost: {cartDetails.totalCost}</p>
                {
                cartDetails.items.map((cart) => (
                    <CartItem cart={cart} showDetails={showDetails} />
                   ))
                }
            </div>
            ) : (
                <CircularProgress /> 
            )
            }
           

        </div>
    )
}

export default CustomerCart;
