import React, { useState } from 'react';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';
import LoginBox from './LoginBox.js';
import RegisterBox from './RegisterBox.js';
import { Autocomplete } from '@material-ui/lab';
//import { TextField, AppBar, Tabs, Tab } from '@material-ui/core';
//import Tabs from 'react-bootstrap/Tabs';
//import Tab from 'react-bootstrap/Tab';
//import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import 'react-tabs/style/react-tabs.css';
require('dotenv').config();

const Login = ({ responsesuccessGoogle, responseerrorGoogle, responseFacebook, setTokenOnSuccess, setType}) => {

  const [chosenElement, setChosenElement] = useState(null);

  const activateRegister = () => {
    setChosenElement(< RegisterBox setTokenOnSuccess = {setTokenOnSuccess} />);
  }

  const activateLogin = () => {
    setChosenElement(< LoginBox setTokenOnSuccess = {setTokenOnSuccess}/>);
  }
    return (
        <div>
          <div>
            <button onClick={activateRegister}>Register</button>
            <button onClick={activateLogin}>Login</button>
          </div>

          <div>
            {chosenElement}
          </div>

      <input type="radio" id="customer" name="accttype" value="customer" checked={setType('customer')} />
      <label for="customer">Customer Account</label>
      <input type="radio" id="seller" name="accttype" value="seller" checked={setType('seller')} />
      <label for="seller">Seller Account</label><br />

      <h1>Login with Google</h1>
      <GoogleLogin
        clientId={process.env.REACT_APP_GOOGLE_CLIENT_ID}
        buttonText="Login with Google"
        onSuccess={responsesuccessGoogle}
        onFailure={responseerrorGoogle}
        cookiePolicy={'single_host_origin'}
      />
      <h1>Login with Facebook</h1>
      <FacebookLogin
        appId={process.env.REACT_APP_FACEBOOK_APP_ID}
        autoLoad={false}
        callback={responseFacebook} 
      />
    </div>
    )
}

export default Login;
