import React, { useState, useEffect } from 'react';
import axios from 'axios';

import { makeStyles, useTheme, withTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import AddIcon from '@material-ui/icons/Add';
import SubtractIcon from '@material-ui/icons/Remove';
import DeleteIcon from '@material-ui/icons/Delete';

import image from '../../../images/image2.JPG';
import { Button } from '@material-ui/core';

const CustomerCart = ({ userid }) => {

    const useStyles = makeStyles((theme) => ({
        root: {
          display: 'flex',
        },
        details: {
          display: 'flex',
          flexDirection: 'column',
          width: '65%',
        },
        content: {
          flex: '1 0 auto',
        },
        cover: {
          width: 151,
        },
        controls: {
          display: 'flex',
          alignItems: 'center',
          paddingLeft: theme.spacing(1),
          paddingBottom: theme.spacing(1),
        },
        container: {
            width: '80%',
            margin: 'auto',
        },
        price: {
            color: 'white',
            fontWeight: 'bold',
            backgroundColor: 'maroon',
            padding: '10px',
            width: '70px',
            borderRadius: '15%',
        }
      }));

      const classes = useStyles();

      const getCartDetails = () => {
        axios({
            method: "GET",
            url: "http://localhost:5000/cart/fetch/"+userid, 
          }).then(res => {
              console.log(res.data);
          });
      }

      useEffect(() => {
        getCartDetails();
        }, [])


    return (
        <div className={classes.container}>
            <h3>This is Customer Cart</h3>
            <Card className={classes.root}>
                <CardMedia
                    className={classes.cover}
                    image={image}
                    title="Live from space album cover"
                />
                <div className={classes.details}>
                    <CardContent className={classes.content}>
                    <Typography component="h5" variant="h5">
                        Black Jeans for Women
                    </Typography>
                    <Typography variant="subtitle1" color="textSecondary">
                        SLVETE Women's Denim Stretchable Mid Rise Jeans
                    </Typography>
                    <Typography variant="subtitle1" className={classes.price}>
                        Rs. 619
                    </Typography>
                    </CardContent>
                    <div className={classes.controls}>
                    <div>
                    <IconButton aria-label="previous">
                        <AddIcon /> 
                    </IconButton>
                    <IconButton aria-label="play/pause">
                        <SubtractIcon />
                    </IconButton>
                    <IconButton aria-label="next">
                        <DeleteIcon />
                    </IconButton>
                    </div>

                    </div>
                </div>
                <div style={{marginTop: '50px', display:'flex', flexDirection:'column'}}>
                    <Typography variant="subtitle1">
                        Qty: 2
                    </Typography>
                    <Button style={{backgroundColor: '#FFC300'}}>Proceed to Buy</Button>
                </div>
            </Card>
        </div>
    )
}

export default CustomerCart;
